import logo from './logo.svg';
import './App.css';
import searchIcon from './assets/searchIcon.png';
import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import {
  Typography,
  Box,
  AppBar,
  Toolbar,
  Container,
  Paper,
  InputBase,
  Button
} from "@material-ui/core";

import {
  Search
} from "@material-ui/icons";


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1
  },
  title: {
    flexGrow: 1,
  },
  paper: {
    padding: 15,
  },
  btn:{
    textTransform:'capitalize'
  },
  appBar:{
    backgroundColor:"#2593C0"
  },
  paper: {
    padding: 15,
  },
  search:{
    flexGrow:1,
    // height:10
  },
  button:{
    backgroundColor:"#2593C0",
    color:"#ffffff",
    textTransform:'capitalize'
  }
}));

function App() {
  const classes = useStyles();

  useEffect(()=>{

  },[]);

  return (
    <div className={classes.root}>
      <AppBar position="static" className={classes.appBar} elevation={0}>
        <Toolbar>
          <Box className={classes.title} ml={2}>

              <Typography variant="h5">Challenge 2</Typography>

          </Box>

        </Toolbar>
      </AppBar>
      <Container maxWidth="sm">
      <Box pt={10}>
            <Paper className={classes.paper}>
              <Box p={0} display="flex" flexDirection="column">
                <Box display="flex" alignItems="center" justifyContent="center">
                <InputBase
                className={classes.search}
                size="small"
                placeholder="Search by Album Id"
                inputProps={{ 'aria-label': 'naked' }}
              /> 
              <Button
              variant="contained"
              size="small"
              elevation={0}
              className={classes.button}
              startIcon={<Search />}>
              Get Photos By Id
              </Button>
                </Box>
              </Box>
             </Paper>
             <Box mt={10} display="flex" alignItems="center" justifyContent="center">
               <img src={searchIcon} width={320}/>
             </Box>
       </Box >

      </Container>


    </div>
  );
}

export default App;
